if not exists(select top 1 1 from sys.schemas where name = 'Marketing')
begin
  execute('create schema Marketing authorization dbo')

  print 'Создана схема Marketing'
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'PrognosisEvents')
begin
    create table Marketing.PrognosisEvents(
        MainEventId int NOT NULL PRIMARY KEY,
        ShowDate date NOT NULL,
        StartInterval time NOT NULL,
        EndInterval time NOT NULL,
        Rating tinyint NOT NULL,
        BackupEventId int NULL,
        WorkerId int NOT NULL,
        DateCreate datetime NOT NULL)

    ALTER TABLE Marketing.PrognosisEvents ADD CONSTRAINT
	    DF_MarketingEvents_DateCreate DEFAULT getdate() FOR DateCreate
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'Markets')
begin
    create table Marketing.Markets(
        Id int PRIMARY KEY IDENTITY,
        MarketId int NOT NULL,
        EventTypeId int NOT NULL,
        Name nvarchar(100) NOT NULL,
        MarketType tinyint NOT NULL,
        ParameterId int NULL)
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'MarketCombinations')
begin
    create table Marketing.MarketCombinations(
        Id int PRIMARY KEY IDENTITY,
        EventTypeId int NOT NULL,
        MarketType int NOT NULL,
        MinValue decimal(4,2) NOT NULL,
        MaxValue decimal(4,2) NOT NULL,
        Combinations nvarchar(MAX) NOT NULL)
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'MarketCoefTypes')
begin
    create table Marketing.MarketCoefTypes(
        CoefTypeId int PRIMARY KEY,
        ShortName nvarchar(150) NULL,
        Description nvarchar(300) NULL,
        ShortDescription nvarchar(300) NULL)
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'Lotteries')
begin
    create table Marketing.Lotteries(
        Id int PRIMARY KEY IDENTITY,
        Name nvarchar(100) NOT NULL,
        Description nvarchar(250) NOT NULL,
        Size tinyint NOT NULL,
        StartDate date NOT NULL,
        EndDate date NOT NULL,
        Active bit NOT NULL,
        WorkerId int NOT NULL,
        DateCreate datetime NOT NULL DEFAULT getdate(),
        IsDeleted bit NOT NULL DEFAULT 0
)
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'LotteryBranches')
begin
    create table Marketing.LotteryBranches(
        Id int PRIMARY KEY IDENTITY,
        LotteryId int NOT NULL,
        BranchId int NOT NULL,
        WorkerId int NOT NULL,
        DateCreate datetime NOT NULL DEFAULT getdate(),
        CONSTRAINT FK_LotteryBranches_Lotteries FOREIGN KEY (LotteryId) REFERENCES Marketing.Lotteries(Id))
end

if not exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_SCHEMA = 'Marketing' and TABLE_NAME = 'LotteryEvents')
begin
    create table Marketing.LotteryEvents(
        Id int PRIMARY KEY IDENTITY,
        LotteryId int NOT NULL,
        EventId int,
        WorkerId int NOT NULL,
        DateCreate datetime NOT NULL DEFAULT getdate(),
        CONSTRAINT FK_LotteryEvents_Lotteries FOREIGN KEY (LotteryId) REFERENCES Marketing.Lotteries (Id))
end

